import React, { useState, useEffect } from 'react'
import { getTheme } from '../api/getTheme'
import { ThemeContent } from '../components/ThemeContent'
import { Link } from 'react-router-dom';

export const ThemeTop = () => {
    const initialState = {
        id: '',
        theme: '',
        comment: '',   
    }

    const[theme, setTheme] = useState(initialState);
    const[loading, setLoading] = useState(true);

    useEffect(() => {
        getTheme()
        .then(d => {
            setTheme(d)
            setLoading(false)
        })
        .catch(e => {
            throw new Error(e)
        })
    },[])

    return(
        <div>
            <h1 className='theme-title'>テーマ一覧</h1>
            
            <div><Link to='/theme/form'className='theme-content'><h1>Submit Themes</h1></Link></div>
            {
                loading ?
                <h1>loading...</h1>
                :
                <div>
                {theme.map( d => <ThemeContent {...d}  /> )}
                </div>
            }
        </div>
    )

}
