import React, { useState } from 'react';
import { postTheme } from '../api/postTheme'; // 追加

export const ThemeForm = () => {
  const initialState = {
    theme: '',
    comment: ''
  };

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postTheme(formData)
      .then((data) => {
        // データの投稿が成功した場合の処理
        console.log('Theme posted successfully:', data);
        // 投稿後のリダイレクトなどの処理を追加することができます
      })
      .catch((error) => {
        // エラーハンドリング
        console.error('Failed to post theme:', error);
      });
  };

  return (
    <div>
      <h2>Theme Form</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Theme:
          <input type="text" name="theme" value={formData.theme} onChange={handleChange} />
        </label>
        <br />
        <label>
          Comment:
          <input type="text" name="comment" value={formData.comment} onChange={handleChange} />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};
