import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getThemeDetail } from '../api/getTheme';
import marked from 'marked';


export const ThemeDetail = () => {
    const initialState = {
        theme: '',
        comment: '',
    };

    const [detail, setDetail] = useState(initialState)
    const [loading, setLoading] = useState(true);
    const { id } = useParams();

    useEffect(()=>{
        getThemeDetail(id)
        .then(d => {
            setDetail(d)
            setLoading(false)
        })
        .catch(e => {
            throw new Error(e)
        })
    },[])
    
    return(
        <div>
            {loading ?
                <h1>loading....</h1>
                :
                <div>
                    <h1>テーマ</h1>
                    <div dangerouslySetInnerHTML={{ __html: `${marked(`${detail.theme}`)}` }} className="detail-content"></div>
                    <h1>主意文</h1>
                    <div dangerouslySetInnerHTML={{ __html: `${marked(`${detail.comment}`)}` }} className="detail-content"></div>
                </div>
            }
        </div>
    )
    
}
