import React, { useState } from 'react';
import { postTheme } from '../api/postTheme';

export const ThemeForm = () => {
  const initialState = {
    theme: '',
    comment: ''
  };

  const [formData, setFormData] = useState(initialState);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postTheme(formData)
      .then((data) => {
        console.log('Theme posted successfully:', data);
        setIsSubmitted(true); // 投稿完了フラグを設定
      })
      .catch((error) => {
        console.error('Failed to post theme:', error);
      });
  };

  return (
    <div className='formwrapper'>
      <h2 className='theme-title'>テーマ投稿</h2>
      {isSubmitted ? ( // 投稿完了時の表示
        <div>
          <p>投稿が完了しました。</p>
          {/* 他の表示やリダイレクトなどの処理を追加 */}
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <label>
            Theme:
            <input type="text" name="theme" value={formData.theme} onChange={handleChange} placeholder="theme"/>
          </label>
          <br />
          <label>
            Comment:
            <input type="text" name="comment" value={formData.comment} onChange={handleChange} />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};
