export const postTheme = async (data) => {
  const token = localStorage.getItem('token'); // 保存したトークンを取得
  const res = await fetch('http://localhost:8000/theme/create/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Token ${token}`, // トークンをヘッダーに含める
    },
    body: JSON.stringify(data),
  });

  return await res.json();
};
