import React from 'react';
import { Link } from 'react-router-dom';

export const StaffThemeContent = ({ theme }) => {
  // 投票数に基づいて項目を並び替え
  const sortedThemes = theme.sort((a, b) => b.votes - a.votes);

  return (
    <div>
      {sortedThemes.map((theme) => (
        <div className="theme-content" key={theme.id}>
          <Link to={`${theme.id}`} className="links">
            <h1>
              {theme.theme}
              <span className="votes">{theme.votes}票</span>
            </h1>
          </Link>
        </div>
      ))}
    </div>
  );
};
