import React, { useState } from 'react';
import { postTheme } from '../api/postTheme';

export const ThemeForm = () => {
  const initialState = {
    theme: '',
    comment: ''
  };

  const [formData, setFormData] = useState(initialState);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postTheme(formData)
      .then((data) => {
        console.log('Theme posted successfully:', data);
        setIsSubmitted(true); // 投稿完了フラグを設定
      })
      .catch((error) => {
        console.error('Failed to post theme:', error);
      });
  };

  return (
    <div className='formwrapper'>
      <h2 className='theme-title'>テーマ投稿</h2>
      {isSubmitted ? ( // 投稿完了時の表示
        <div>
          <p>投稿が完了しました。</p>
          {/* 他の表示やリダイレクトなどの処理を追加 */}
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
           <div className='detail-theme-wrapper'>
            <div className="detail-theme">趣意文</div>
            <label>
            <input type="text" name="theme" value={formData.theme} onChange={handleChange} placeholder="テーマ"/>
          </label>
          <br />
          <label>
            <input type="text" name="comment" value={formData.comment} onChange={handleChange} placeholder="趣意文"/>
          </label>
          <br />
          <button type="submit" id='submit'>Submit</button>
          </div>
          <div className="detail-content">{detail.comment}</div>
        </form>
      )}
    </div>
  );
};
