import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

const Header = ({ isLoggedIn, isStaff }) => {
  const [isMenuExpanded, setIsMenuExpanded] = useState(false);

  const toggleMenu = () => {
    setIsMenuExpanded(!isMenuExpanded);
  };

  return (
    <header className={`header-wrapper ${isMenuExpanded ? 'header-expanded' : 'header-collapsed'}`}>
      <div>
        <Link to="/" className="link-top">
          Forsteri
        </Link>
      </div>
      <div>
        {isLoggedIn ? (
          <>
            <Link to="/logout" className="links">
              Logout
            </Link>
            <Link to="/theme" className="links">
              Theme
            </Link>
            {isStaff && (
              <Link to="/staff" className="links">
                Staff
              </Link>
            )}
          </>
        ) : (
          <>
            <Link to="/signup" className="links">
              Signup
            </Link>
            <Link to="/login" className="links">
              Login
            </Link>
          </>
        )}
      </div>
      <div className="hamburger-icon" onClick={toggleMenu}>
        <i className={`fas ${isMenuExpanded ? 'fa-times' : 'fa-bars'}`}></i>
      </div>
      {isMenuExpanded && (
        <div className="menu-links">
          {isLoggedIn ? (
            <>
              <Link to="/logout" className="links">
                Logout
              </Link>
              <Link to="/theme" className="links">
                Theme
              </Link>
              {isStaff && (
                <Link to="/staff" className="links">
                  Staff
                </Link>
              )}
            </>
          ) : (
            <>
              <Link to="/signup" className="links">
                Signup
              </Link>
              <Link to="/login" className="links">
                Login
              </Link>
            </>
          )}
        </div>
      )}
    </header>
  );
};

export default Header;
