import React, { useState, useEffect } from 'react'
import { getTheme } from '../api/getTheme'
import { ThemeContent } from '../components/ThemeContent'

export const ThemeTop = () => {
    const [themes, setThemes] = useState([]); // themes ステートを空の配列として初期化

    useEffect(() => {
        getTheme()
        .then(d => {
            setThemes(d); // テーマの配列を設定
        })
        .catch(e => {
            throw new Error(e)
        })
    }, [])

    return (
        <div>
            {
                themes.length === 0 ? (
                    <h1>loading...</h1>
                ) : (
                    <div>
                        {themes.map(theme => (
                            <ThemeContent key={theme.id} theme={theme} /> 
                        ))}
                    </div>
                )
            }
        </div>
    )
}
