import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getThemeDetail } from '../api/getTheme';

export const ThemeContent = ({ theme }) => {
  const [detail, setDetail] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchThemeDetail = async () => {
      try {
        const response = await getThemeDetail(theme.id);
        setDetail(response);
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchThemeDetail();
  }, [theme.id]);

  return (
    <div>
      {loading ? (
        <h1>loading....</h1>
      ) : (
        <div>
          <Link to={`/theme/${theme.id}`}>
            <h1>{theme.theme}</h1>
          </Link>
          <h2>テーマ詳細</h2>
          {detail && (
            <div>
              <p>テーマ: {detail.theme}</p>
              <p>コメント: {detail.comment}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};
