import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Header } from './Header';
import { Top } from './Top';
import { ThemeTop } from './theme/pages/ThemeTop';
import { ThemeDetail } from './theme/pages/ThemeDetails';
import { ThemeForm } from './theme/pages/ThemeForm';
import { SignupForm } from './auth/pages/SignupForm';
import { LoginForm } from './auth/pages/LoginForm';
import { LogoutForm } from './auth/pages/LogoutForm';

export const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch('http://localhost:8000/auth/check-login-status', {
          credentials: 'include' // Send cookies along with the request
        });

        if (response.ok) {
          setIsLoggedIn(true);
        } else {
          setIsLoggedIn(false);
        }
      } catch (error) {
        console.error('Failed to check login status:', error);
      } finally {
        setLoading(false);
      }
    };

    checkLoginStatus();
  }, []);

  const handleLogin = (token) => {
    // ログイン時に認証トークンを保存し、ログイン状態を設定
    localStorage.setItem('token', token);
    setIsLoggedIn(true);
  };

  const handleLogout = async () => {
    try {
      await fetch('http://localhost:8000/auth/logout', {
        method: 'POST',
        credentials: 'include' // Send cookies along with the request
      });

      // ログアウト時に認証トークンを削除し、ログイン状態を更新
      localStorage.removeItem('token');
      setIsLoggedIn(false);
    } catch (error) {
      console.error('Failed to logout:', error);
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Router>
        <Header isLoggedIn={isLoggedIn} />
        <div>
          <Routes>
            <Route path="/" element={<Top />} />
            <Route path="/theme" element={<ThemeTop />} />
            <Route path="/theme/:id" element={<ThemeDetail />} />
            <Route path="/theme/form" element={<ThemeForm />} />
            <Route path="/auth/signup" element={<SignupForm />} />
            <Route
              path="/auth/login"
              element={<LoginForm onLogin={handleLogin} />}
            />
            <Route
              path="/auth/logout"
              element={<LogoutForm onLogout={handleLogout} />}
            />
            <Route path="*" element={<h4>not found...</h4>} />
          </Routes>
        </div>
      </Router>
    </div>
  );
};

export default App;
