const handleSubmit = (e) => {
  e.preventDefault();
  const token = localStorage.getItem('token'); // Retrieve the token from localStorage

  postTheme(formData, token)
    .then((data) => {
      console.log('Theme posted successfully:', data);
      setIsSubmitted(true);
    })
    .catch((error) => {
      console.error('Failed to post theme:', error);
    });
};
