import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Header } from './Header';
import { Top } from './Top';
import { ThemeTop } from './theme/pages/ThemeTop';
import { ThemeDetail } from './theme/pages/ThemeDetails';
import { ThemeForm } from './theme/pages/ThemeForm'; // 追加
import { SignupForm } from './auth/pages/SignupForm'; // 追加
import { Login } from './auth/pages/Login'; // 追加
import { LogoutForm } from './auth/pages/LogoutForm'; // 追加


export const apiURL = 'http://localhost:8000/auth/api/';
export const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div>
      <Router>
        <Header isLoggedIn={isLoggedIn} />
        <div>
          <Routes>
            <Route path="/" element={<Top />} />
            <Route path="/theme" element={<ThemeTop />} />
            <Route path="/theme/:id" element={<ThemeDetail />} />
            <Route path="/theme/form" element={<ThemeForm />} />
            <Route
              path="/auth/signup"
              element={<SignupForm onSignup={() => setIsLoggedIn(true)} />}
            />
            <Route exact path="/auth/login" component={Login} />
            <Route
              path="/auth/logout"
              element={<LogoutForm onLogout={() => setIsLoggedIn(false)} />}
            />
            <Route path="*" element={<h4>not found...</h4>} />
          </Routes>
        </div>
      </Router>
    </div>
  );
};

export default App;
