import React from 'react';
import axios from 'axios';
import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import { apiURL } from '../../App';

const Signup = () => {
  const navigate = useNavigate();

  const { register, handleSubmit, formState: { errors } } = useForm();

  const handleSignup = async (data) => {
    try {
      const response = await axios.post(`${apiURL}signup/`, {
        email: data.email,
        name: data.name,
        password: data.password,
      });
      console.log(response.data);
      alert('ユーザーが作成されました');
      navigate('/login');
    } catch (error) {
      console.log('error', error);
      alert('ユーザーの作成に失敗しました');
    }
  };

  return (
    <div className="signup-wrapper">
      <div className="signup-form">
        <form onSubmit={handleSubmit(handleSignup)}>
          <label htmlFor="email">Email:</label>
          <input className="form-control" type="email" {...register('email', { required: true })} />
          {errors.email && <span className="error">Emailを入力してください</span>}

          <label htmlFor="name">Name:</label>
          <input className="form-control" type="text" {...register('name', { required: true })} />
          {errors.name && <span className="error">名前を入力してください</span>}

          <label htmlFor="password">Password:</label>
          <input className="form-control" type="password" {...register('password', { required: true })} />
          {errors.password && <span className="error">パスワードを入力してください</span>}

          <input className="btn btn-secondary" type="submit" value="Signup" />
        </form>
      </div>
    </div>
  );
};

export default Signup;
