import React, { useState, useEffect } from 'react'
import { getTheme } from '../api/getTheme'
import { ThemeContent } from '../components/ThemeContent'

export const ThemeTop = () => {
    const initialState = {
        id: '',
        theme: '',
        comment: '',   
    }

    const [theme, setTheme] = useState(initialState);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        getTheme()
        .then(d => {
            setTheme(d[0])  // 最初の要素を設定
            setLoading(false)
        })
        .catch(e => {
            throw new Error(e)
        })
    }, [])

    return (
        <div>
            {
                loading ?
                <h1>loading...</h1>
                :
                <div>
                    <ThemeContent {...theme} /> {/* テーマコンポーネントに `theme` を渡す */}
                </div>
            }
        </div>
    )
}
